#pragma config(Hubs,  S1, HTMotor,  none,     none,     none)
#pragma config(Sensor, S1,     ,               sensorI2CMuxController)
#pragma config(Motor,  mtr_S1_C1_1,     motorLiftRight, tmotorTetrix, openLoop)
#pragma config(Motor,  mtr_S1_C1_2,     motorLiftLeft, tmotorTetrix, openLoop)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

#include "MotorConfig.h"

#include "Math.c"
#include "JoystickUtil.c"
#include "Joystick.c"
#include "DrivingModes.c"

void config()
{
	MOTOR_CONFIG.lift.left			= motorLiftLeft;
	MOTOR_CONFIG.lift.right			= motorLiftRight;
	MOTOR_CONFIG.lift.bucketLift	= NO_MOTOR;
	MOTOR_CONFIG.lift.isEnabled		= true;

	MOTOR_CONFIG.wheels.frontLeft	= NO_MOTOR;
	MOTOR_CONFIG.wheels.frontRight	= NO_MOTOR;
	MOTOR_CONFIG.wheels.backLeft	= NO_MOTOR;
	MOTOR_CONFIG.wheels.backRight	= NO_MOTOR;
	MOTOR_CONFIG.wheels.isEnabled	= false;

	MOTOR_CONFIG.flag.id			= NO_MOTOR;
	MOTOR_CONFIG.flag.isEnabled		= false;
}

task main()
{
	config();
	waitForStart();
	StartTask(joystickListener);

	while (true);
}
